@use '../../../../styles/helpers/hover';

.toggle-slider {
    @include themify.dd-themify() {
        height: themify.dd-configuration('inputs', default-size);
    }
}

.disabled {
    cursor: default !important;
    opacity: 0.6 !important;
}

.switch {
    position: relative;
    display: inline-block;
    width: 53px;
    max-width: none;
    height: 24px;
}
.switch input {
    opacity: 0;
    width: 0;
    height: 0;
}
.slider {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    -webkit-transition: 0.4s;
    transition: 0.4s;
    border-radius: 8px;

    @include themify.dd-themify() {
        background-color: themify.dd-color('primary-200');
    }
}
.slider:before {
    position: absolute;
    content: '';
    height: 18px;
    width: 18px;
    left: 5px;
    bottom: 3px;
    -webkit-transition: 0.4s;
    transition: 0.4s;
    border-radius: 50%;

