@use '../../../../styles/variables/breakpoints';
@use '../../../../styles/themes/themify';

// Generic styles for the toolbar.
.dd-topbar-top {
    white-space: nowrap;
    position: fixed;
    left: 0;
    top: 0;
    right: 0;
    z-index: 2000;
    height: 56px;

    padding-left: 12px;
    padding-right: 12px;

    @include breakpoints.media-breakpoint('xxs') {
        padding-left: 4px;
        padding-right: 4px;
    }

    .dd-topbar-menu-button-text {
        @include breakpoints.media-breakpoint('lt-lg') {
            display: none;
        }
    }

    .dd-topbar-logo-with-bg {
        padding-left: 8px;
        padding-right: 8px;

        @include breakpoints.media-breakpoint('gt-lg') {
            padding-left: 0px;
        }
    }

    // Hide all borders from the toolbar on very small screens.
    .border-right.dd-white-border.h-100-p {
        @include breakpoints.media-breakpoint('xxs') {
            display: none;
        }
    }

    .dd-topbar-left-container {
        gap: 8px;

        // The area should grow on smaller screens
        @include breakpoints.media-breakpoint('gt-md') {
            flex: 1 1 100%;
            box-sizing: border-box;
            max-width: 100%;
        }

        // Logo container
        .dd-topbar-logo-container {
            gap: 12px;

            // Logo image and the description that is rendered below it.
            .dd-topbar-logo-image {
                display: flex;
                align-items: center;
                position: relative;

                .dd-topbar-description-container {
                    position: absolute;
                    padding: 0 4px;
                    bottom: 4px;
                    left: 50%;
                    transform: translateX(-50%);

                    @include themify.dd-themify() {
                        background-color: themify.dd-color('primary-900', 0.6) !important;
                    }
                }
            }
        }

        // Define that logo can only be certain width.
        .dd-topbar-logo-image,
        .dd-topbar-logo {
            max-width: 140px;
        }

        // Make the logo smaller on very small screens.
        @include breakpoints.media-breakpoint('xxs') {
            .dd-topbar-logo-image,
            .dd-topbar-logo {
                max-width: 80px;
            }
        }

        // Title can be hidden in mobile.
        @include breakpoints.media-breakpoint('lt-md') {
            .dd-topbar-title-container {
                display: none;
            }
        }
    }

    // All the styles that are used in the center of the toolbar.
    // Contains only the search bar currently.
    .dd-topbar-center-container {
        padding-left: 12px;
        padding-right: 12px;

        @include breakpoints.media-breakpoint('lt-md') {
            display: none;
        }

        @include breakpoints.media-breakpoint('gt-md') {
            max-width: 400px;
            width: 100%;
        }

        @include breakpoints.media-breakpoint('lt-lg') {
            flex: 1 1 100%;
            box-sizing: border-box;
            max-width: 100%;
        }
    }

    // Styles for the additional buttons in topbar. Same style is used for menu and search buttons.
    .additional-button {
        position: relative;
        padding: 12px;

        // Generic badge for additional buttons.
        .additional-button-badge {
            position: absolute;

            right: 4px;
            top: 4px;
            text-transform: uppercase;
            padding: 2px;
            border-radius: 16px;
            min-width: 16px;
        }
    }

    // All the styles that are used in the right side of the toolbar.
    .dd-topbar-right-container {
        gap: 8px;

        @include breakpoints.media-breakpoint('gt-md') {
            flex: 1 1 100%;
            box-sizing: border-box;
            max-width: 100%;
        }

        // The container for the search bar.
        .dd-topbar-search-container {
            padding: 12px;
        }

        // Show the search button on smaller screens.
        @include breakpoints.media-breakpoint('gt-sm') {
            .dd-topbar-search-container {
                display: none;
            }
        }

        // Hide all unnecessary additional buttons on smaller screens.
        @include breakpoints.media-breakpoint('lt-sm') {
            .dd-topbar-low-priority-button {
                display: none;
            }
        }

        // Top left user button for the toolbar.
        .dd-topbar-user-button {
            gap: 8px;
            padding: 8px;

            // Add support for user image. This is a circle image.
            // The image is 32px by 32px make sure it's not overflowing.
            .dd-topbar-user-image {
                overflow: hidden;

                img {
                    width: 100%;
                    height: 100%;
                    overflow: hidden;
                }
            }

            // Remove the text from the UI on smaller screens.
            .dd-topbar-user-button-text {
                @include breakpoints.media-breakpoint('lt-lg') {
                    display: none;
                }
            }
        }
    }

    // -----------------------------------------------------------------------------------------------------
    // Create a search bar that is fixed to the top of the screen for mobile devices.
    // In mobile design there is a button that opens the search bar.
    // -----------------------------------------------------------------------------------------------------
    .mobile-search-bar {
        position: fixed;
        top: 0;
        left: 0;
        width: 100vw;
        height: 56px;

        z-index: 2000;

        display: flex;
        flex-direction: row;
        align-items: center;
        gap: 12px;

        padding-left: 12px;
        padding-right: 12px;
    }
}

