import { NgModule } from '@angular/core';
import { RouterModule } from '@angular/router';
import { ChipModule } from '@digitaldelightsoy/design-library/src/chip';
import { IconModule } from '@digitaldelightsoy/design-library/src/icon';
import { NavigationModule } from '@digitaldelightsoy/design-library/src/navigation';
import { SelectModule } from '@digitaldelightsoy/design-library/src/select';
import { TabNavigationPreviousComponent } from './components/tab-navigation-previous/tab-navigation-previous.component';
import { TabNavigationV2Component } from './components/tab-navigation-v2/tab-navigation-v2.component';
import { TabNavigationComponent } from './components/tab-navigation/tab-navigation.component';
import { TabComponent } from './components/tab/tab.component';
import { TabsV2Component } from './components/tabs-v2/tabs-v2.component';
import { TabsComponent } from './components/tabs/tabs.component';

const components: any[] = [
    TabNavigationComponent, //
    TabNavigationPreviousComponent,
    TabsComponent,
    TabComponent,
    TabNavigationV2Component,
    TabsV2Component,
];

@NgModule({
    declarations: [...components],
    exports: [...components],
    imports: [
        CommonModule, //
        IconModule,
        RouterModule,
        NavigationModule,
        SelectModule,
        ChipModule,
    ],
})
export class TabNavigationModule {}

import { CommonModule } from '@angular/common';
import { NgModule } from '@angular/core';
import { RouterModule } from '@angular/router';
import { IconModule } from '@digitaldelightsoy/design-library/src/icon';
import { TileFixedComponent } from './components/tile-fixed/tile-fixed.component';
import { TileFooterComponent } from './components/tile-footer/tile-footer.component';
import { TileComponent } from './components/tile/tile.component';

@NgModule({
    declarations: [TileComponent, TileFixedComponent, TileFooterComponent],
    exports: [TileComponent, TileFixedComponent, TileFooterComponent],
    imports: [
        CommonModule, //
        IconModule,
        RouterModule,
    ],
})
export class TileModule {}

import { CommonModule } from '@angular/common';
import { NgModule } from '@angular/core';
import { TooltipDirective } from './tooltip.directive';

@NgModule({
    declarations: [TooltipDirective],
    exports: [TooltipDirective],
    imports: [CommonModule],
})
export class TooltipModule {}

