    // This animation can be added to parent DOM, when initial animation should be skipped.
    blockInitialRenderAnimation: trigger(
        'blockInitialRenderAnimation', //
        [
            transition(
                ':enter', //
                []
            ),
        ]
    ),
    slideFromTop: trigger('slideFromTop', [
        // Transition states.
        state(
            'in-top',
            style({
                transform: 'translate3d(0,0,0)',
            })
        ),
        state(
            'hide',
            style({
                transform: 'translate3d(0, -100%, 0)',
            })
        ),
        state(
            'default',
            style({
                transform: 'translate3d(0, 0, 0)',
            })
        ),
        // Void state (hidden).
        state(
            '*',
            style({
                transform: 'translate3d(0, -100%, 0)',
            })
        ),
        transition(
            ':enter',
            animate(
                `${animationInTime}ms ease-in-out`, //
                keyframes([
                    style({ transform: 'translate3d(0, -100%, 0)' }), //
                    style({ transform: 'translate3d(0,0,0)' }), //
                ])
            )
        ),
        transition(
            'in-top => hide',
            animate(
                `${animationOutTime}ms ease-in-out`, //
                keyframes([
                    style({ transform: 'translate3d(0,0,0)' }), //
                    style({ transform: 'translate3d(0, -100%, 0)' }), //
                ])
            )
        ),
    ]),
    slideFromLeft: trigger('slideFromLeft', [
        state(
            'in-left',
            style({
                transform: 'translate3d(0,0,0)',
            })
        ),
        state(
            'hide',
            style({
                transform: 'translate3d(0, -100%, 0)',
            })
        ),
        state(
            '*',
            style({
                transform: 'translate3d(0, -100%, 0)',
            })
        ),
        transition(
            ':enter',
            animate(
                `${animationInTime}ms ease-in-out`, //
                keyframes([
                    style({ transform: 'translate3d(-100%,0,0)' }), //
                    style({ transform: 'translate3d(0,0,0)' }), //
                ])
            )
        ),
        // Left slide transition.
        transition(
            'in-left => hide',
            animate(
                `${animationOutTime}ms ease-in-out`, //
                keyframes([
                    style({ transform: 'translate3d(0,0,0)' }), //
                    style({ transform: 'translate3d(-100%, 0, 0)' }), //
                ])
            )
        ),
    ]),
    slideFromRight: trigger('slideFromRight', [
        state(
            'in-right',
            style({
                transform: 'translate3d(0,0,0)',
            })
        ),
        state(
            'hide',
            style({
                transform: 'translate3d(100%, 0, 0)',
            })
        ),
        state(
            'default',
            style({
                transform: 'translate3d(0,0,0)',
            })
        ),
        state(
            '*',
            style({
                transform: 'translate3d(100%, 0, 0)',
            })
        ),
        transition(
            ':enter',
            animate(
                `${animationInTime}ms ease-in-out`, //
                keyframes([
                    style({ transform: 'translate3d(100%,0,0)' }), //
                    style({ transform: 'translate3d(0,0,0)' }), //
                ])
            )
        ),
        // Right slide transition.
        transition(
            'in-right => hide',
            animate(
                `${animationInTime}ms ease-in-out`, //
                keyframes([
                    style({ transform: 'translate3d(0,0,0)' }), //
                    style({ transform: 'translate3d(100%, 0, 0)' }), //
                ])
            )
        ),
    ]),
    slideInOut: trigger('slideInOut', [
        // Transition states.
        state(
            'in-top',
            style({
                transform: 'translate3d(0,0,0)',
                'z-index': 20000001,
            })
        ),
        state(
            'in-left',
            style({
                transform: 'translate3d(0,0,0)',
                'z-index': 20000001,
            })
        ),
        state(
            'hide',
            style({
                transform: 'translate3d(0, -100%, 0)',
                'z-index': -1,
            })
        ),
        // Top (modal) transition.
        transition(
            'in-top => hide',
            animate(
                `${animationOutTime}ms ease-in-out`, //
                keyframes([
                    style({ transform: 'translate3d(0,0,0)' }), //
                    style({ transform: 'translate3d(0, -100%, 0)' }), //
                ])
            )
        ),
        transition(
            'hide => in-top',
            animate(
                `${animationInTime}ms ease-in-out`, //
                keyframes([
                    style({ transform: 'translate3d(0, -100%, 0)', 'z-index': 20000001 }), //
                    style({ transform: 'translate3d(0,0,0)' }), //
                ])
            )
        ),
        // Left slide transition.
        transition(
            'in-left => hide',
            animate(
                `${animationOutTime}ms ease-in-out`, //
                keyframes([
                    style({ transform: 'translate3d(0,0,0)' }), //
                    style({ transform: 'translate3d(-100%, 0, 0)' }), //
                ])
            )
        ),
        transition(
            'hide => in-left',
            animate(
                `${animationInTime}ms ease-in-out`, //
                keyframes([
                    style({ transform: 'translate3d(-100%,0,0)', 'z-index': 20000001 }), //
                    style({ transform: 'translate3d(0,0,0)' }), //
                ])
            )
        ),
    ]),
    backdropInOut: trigger('backdropInOut', [
        state(
            'in',
            style({
                backgroundColor: '#5555',
            })
        ),
        state(
            'out',
            style({
                backgroundColor: 'transparent',
            })
        ),
        transition('in => out', animate(`${animationOutTime}ms ease-in-out`)),
        transition('out => in', animate(`${animationInTime}ms ease-in-out`)),
    ]),
}

